defaultSearch:
SELECT ?scientificName ?authorship ?taxonRank ?taxonomicStatus ?kingdom
WHERE {
	?subject rdf:type ?type ;
  	dwc:scientificName ?scientificName ;
    dwc:scientificNameAuthorship ?authorship ;
    dwc:taxonRank ?taxonRank ;
    dwc:kingdom ?kingdom ;
    dwc:taxonomicStatus ?taxonomicStatus .

  FILTER(CONTAINS(str(?scientificName), "para"))
} LIMIT 100
