from rake_nltk import Rake
import yake
import nltk
from nltk.corpus import stopwords

class TopicModel:

    def __init__(self):
        nltk.download('stopwords')
        nltk.download('punkt')
        self.stop_words = set(stopwords.words('english')).union(set(stopwords.words('german')))

    def remove_stopwords(self, text):
        words = text.split()
        filtered_words = [word for word in words if word.lower() not in self.stop_words]
        return ' '.join(filtered_words)

    def extract_keywords_rake(self, document, top_n=10):
        '''Simple topic modelling with key-phrases. Returns a list of top phrases.'''
        r = Rake()
        r.extract_keywords_from_text(document)
        keywords_with_scores = r.get_ranked_phrases_with_scores()
        top_keywords = [kw for score, kw in keywords_with_scores[:top_n]]
        return top_keywords

    def extract_keywords_yake(self, document, top_n=10):
        '''Simple topic modelling with key-words. Returns a list of words.'''
        # Remove stopwords
        document = self.remove_stopwords(document)
        # Initialize YAKE keyword extractor
        kw_extractor = yake.KeywordExtractor()
        keywords_with_scores = kw_extractor.extract_keywords(document)
        top_keywords = [kw for kw, score in keywords_with_scores[:top_n]]
        return top_keywords

